import {
  useState,
  // useEffect, 
  useReducer
} from 'react';

import Card from '../UI/Card/Card';
import classes from './Login.module.css';
import Button from '../UI/Button/Button';

const INITIAL_INPUT_STATE = {
  value: '',
  // isValid: false
}

const emailReducer = (state, action) => {
  if (action.type === 'USER_INPUT')
    return {
      value: action.value,
      isValid: action.value.includes('@')
    }

  return INITIAL_INPUT_STATE;
}

const passwordReducer = (state, action) => {
  if (action.type === 'USER_INPUT')
    return {
      value: action.value,
      isValid: action.value.trim().length > 6
    }

  return INITIAL_INPUT_STATE;
}

const Login = ({ onLogin }) => {
  // const [enteredEmail, setEnteredEmail] = useState('');
  // const [emailIsValid, setEmailIsValid] = useState();
  const [enteredPassword, setEnteredPassword] = useState('');
  const [passwordIsValid, setPasswordIsValid] = useState();
  const [formIsValid, setFormIsValid] = useState(false);

  const [email, dispatchEmail] = useReducer(emailReducer, INITIAL_INPUT_STATE);
  const [password, dispatchPassword] = useReducer(passwordReducer, INITIAL_INPUT_STATE)

  // useEffect(() => {
  //   const timeoutId = setTimeout(() => {
  //     console.log('Login useEffect')
  //     setFormIsValid(
  //       enteredEmail.includes('@') && enteredPassword.trim().length > 6
  //     );
  //   }, 500)

  //   return () => {
  //     console.log('Login useEffect cleanup')
  //     clearTimeout(timeoutId)
  //   }
  // }, [enteredEmail, enteredPassword])

  const emailChangeHandler = (event) => {
    console.log('emailChangeHandler')
    // setEnteredEmail(event.target.value);
    dispatchEmail({ type: 'USER_INPUT', value: event.target.value });

    // setFormIsValid(
    //   event.target.value.includes('@') && enteredPassword.trim().length > 6
    // );
  };

  const passwordChangeHandler = (event) => {
    setEnteredPassword(event.target.value);

    setFormIsValid(
      email.isValid && event.target.value.trim().length > 6
    );
  };

  // const validateEmailHandler = () => {
  //   setEmailIsValid(enteredEmail.includes('@'));
  // };

  const validatePasswordHandler = () => {
    setPasswordIsValid(enteredPassword.trim().length > 6);
  };

  const submitHandler = (event) => {
    event.preventDefault();
    onLogin(email.value, enteredPassword);
  };

  console.log('email', email)
  return (
    <Card className={classes.login}>
      <form onSubmit={submitHandler}>
        <div
          className={`${classes.control} ${email.isValid === false ? classes.invalid : ''
            }`}
        >
          <label htmlFor="email">E-Mail</label>
          <input
            type="email"
            id="email"
            value={email.value}
            onChange={emailChangeHandler}
          // onBlur={validateEmailHandler}
          />
        </div>
        <div
          className={`${classes.control} ${passwordIsValid === false ? classes.invalid : ''
            }`}
        >
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            value={enteredPassword}
            onChange={passwordChangeHandler}
            onBlur={validatePasswordHandler}
          />
        </div>
        <div className={classes.actions}>
          <Button type="submit" className={classes.btn} disabled={!formIsValid}>
            Login
          </Button>
        </div>
      </form>
    </Card>
  );
};

export default Login;
