import { createSlice } from "@reduxjs/toolkit";

const slice = createSlice({
  name: 'ui',
  initialState: {
    isCartVisible: false
  },
  reducers: {
    toggleCart(state) {
      state.isCartVisible = !state.isCartVisible
    }
  }
});

export const uiActions = slice.actions;

export default slice.reducer;
