import { createSlice } from "@reduxjs/toolkit";

const slice = createSlice({
  name: 'cart',
  initialState: {
    // items: [{
    //   title: 'Test item',
    //   quantity: 2,
    //   price: 12,
    //   total: 24
    // },
    // {
    //   title: 'Test item 2',
    //   quantity: 3,
    //   price: 12,
    //   total: 36
    // },
    // {
    //   title: 'Test item 3',
    //   quantity: 10,
    //   price: 12,
    //   total: 120
    // }],
    items: [],
    quantity: 0,
    price: 0
  },
  reducers: {
    addToCart(state, action) {
      const { payload } = action;
      const existingCartItem = state.items.find(item => item.id === payload.id);

      if (existingCartItem) {
        const quantity = existingCartItem.quantity + 1
        const newCartItem = {
          ...existingCartItem,
          quantity,
          total: quantity * existingCartItem.price,
        }

        state.items = state.items.map(item => item.id === existingCartItem.id ? newCartItem : item);
        state.price = state.price + existingCartItem.price;
      } else {
        const cartItem = {
          ...payload,
          total: payload.price,
          quantity: 1
        };

        const newItems = [
          cartItem,
          ...state.items
        ];

        state.items = newItems
      }

      state.quantity = state.quantity + 1;
      state.price = state.price + payload.price;
    },
    removeFromCart(state, payload) {
      console.log('removeFromCart')
    }
  }
})

export const cartActions = slice.actions;
export default slice.reducer;
