import { createSlice } from "@reduxjs/toolkit";

const slice = createSlice({
  name: 'cart',
  initialState: {
    items: [],
    quantity: 0,
    price: 0
  },
  reducers: {
    addToCart(state, { payload }) {
      const existingCartItem = state.items.find(item => item.id === payload.id);

      if (existingCartItem) {
        existingCartItem.quantity++;
        existingCartItem.total += existingCartItem.price;
        state.price = state.price + existingCartItem.price;
      } else {
        state.items.push({
          ...payload,
          total: payload.price,
          quantity: 1
        })
      }

      state.quantity = state.quantity + 1;
      state.price = state.price + payload.price;
    },
    removeFromCart(state, { payload }) {
      console.log('removeFromCart')
      const existingCartItem = state.items.find(item => item.id === payload);

      if (existingCartItem.quantity > 1) {
        state.items = state.items.map(item => {

          if (item.id === payload)
            return ({
              ...item,
              quantity: item.quantity - 1,
              total: item.total - item.price
            })

          return item;
        })
      } else
        state.items = state.items.filter(item => item.id !== payload)

      state.quantity = state.quantity - 1;
      state.price = state.price - existingCartItem.price;

    }
  }
})

export const cartActions = slice.actions;
export default slice.reducer;
