import { createSlice } from "@reduxjs/toolkit";

const slice = createSlice({
  name: 'cart',
  initialState: {
    // items: [{
    //   title: 'Test item',
    //   quantity: 2,
    //   price: 12,
    //   total: 24
    // },
    // {
    //   title: 'Test item 2',
    //   quantity: 3,
    //   price: 12,
    //   total: 36
    // },
    // {
    //   title: 'Test item 3',
    //   quantity: 10,
    //   price: 12,
    //   total: 120
    // }],
    items: [],
    quantity: 0,
    price: 0
  },
  reducers: {
    addToCart(state, { payload }) {
      const existingCartItem = state.items.find(item => item.id === payload.id);

      if (existingCartItem) {
        const quantity = existingCartItem.quantity + 1
        const newCartItem = {
          ...existingCartItem,
          quantity,
          total: quantity * existingCartItem.price,
        }

        state.items = state.items.map(item => item.id === existingCartItem.id ? newCartItem : item);
        state.price = state.price + existingCartItem.price;
      } else {
        const cartItem = {
          ...payload,
          total: payload.price,
          quantity: 1
        };

        const newItems = [
          cartItem,
          ...state.items
        ];

        state.items = newItems
      }

      state.quantity++;
      state.price = state.price + payload.price;
    },
    removeFromCart(state, { payload }) {
      console.log('removeFromCart')
      const existingCartItem = state.items.find(item => item.id === payload);

      if (existingCartItem.quantity > 1) {
        // Decrement item's quantity
        state.items = state.items.map(item => {
          if (item.id === payload)
            return ({
              ...item,
              quantity: item.quantity - 1
            })

          return item;
        })
      } else
        state.items = state.items.filter(item => item.id !== payload)

      state.quantity--;
      state.price = state.price - existingCartItem.price;

    }
  }
})

export const cartActions = slice.actions;
export default slice.reducer;
