import { useState } from "react";
import { Link, json, useSearchParams } from "react-router-dom";

import classes from "./AuthForm.module.css";

const AuthForm = () => {
  const [params] = useSearchParams();
  const [inputs, setInputs] = useState({ email: "", password: "" });
  const [responseError, setResponseError] = useState();

  const isLogin = params.get("isLogin");
  const authLink = isLogin ? "" : "?isLogin=1";

  const inputHandler = (e) => {
    const { name, value } = e.target;

    setInputs((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const submitHandler = async (e) => {
    e.preventDefault();
    console.log("submitHandler inputs", inputs);
    const url = `http://localhost:8080/${isLogin ? "login" : "signup"}`;

    const response = await fetch(url, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        email: "test@gmail.com",
        password: "123456!@#$%^",
      }),
    });

    console.log("response", response);
    const data = await response.json();

    if (!response.ok) {
      setResponseError(response.errors);
      return;
    }

    console.log("data", data);
  };

  return (
    <>
      <form className={classes.form} onSubmit={submitHandler}>
        <h1>{isLogin ? "Log in" : "Create a new user"}</h1>
        <p>
          <label htmlFor="email">Email</label>
          <input
            id="email"
            type="email"
            name="email"
            required
            value={inputs.email}
            onChange={inputHandler}
          />
        </p>
        <p>
          <label htmlFor="image">Password</label>
          <input
            id="password"
            type="password"
            name="password"
            required
            value={inputs.password}
            onChange={inputHandler}
          />
        </p>
        <div className={classes.actions}>
          <Link to={authLink} type="button">
            {isLogin
              ? "No account? Register"
              : "Already have an account? Login"}
          </Link>
          <button>{isLogin ? "Login" : "Register"}</button>
        </div>
      </form>
    </>
  );
};

export default AuthForm;
